import React from "react";
import { motion } from "framer-motion";
import { Github, ExternalLink } from "lucide-react";

const projects = [
  {
    name: "MOODIFY – AI Song Recommender",
    description: "Mood-based song suggestions using emojis & Spotify API.",
    github: "https://github.com/Atharv28ye/moodify-song-recommender",
    live: "#"
  },
  {
    name: "AI Tokenizer Tool",
    description: "Estimate token usage & cost for GPT models in real-time.",
    github: "https://github.com/Atharv28ye/ai-tokenizer",
    live: "#"
  },
  {
    name: "SmartStudy AI",
    description: "Full-stack GPT-powered study assistant for students.",
    github: "https://github.com/Atharv28ye/Smartstudyai",
    live: "#"
  },
  {
    name: "Custom LLM: GPT Genius",
    description: "Built a custom LLM from scratch—tokenizer & math engine.",
    github: "https://github.com/Atharv28ye/llm-core-full",
    live: "#"
  },
  {
    name: "Portfolio Website",
    description: "React + Tailwind portfolio showcasing my projects.",
    github: "https://github.com/Atharv28ye/blog-platform",
    live: "#"
  }
];

const cardVariants = {
  hidden: { opacity: 0, y: 40 },
  visible: (i) => ({
    opacity: 1,
    y: 0,
    transition: { delay: i * 0.2, type: "spring", stiffness: 100 }
  })
};

const Projects = () => (
  <section id="projects" className="bg-gray-50 py-24 px-6 md:px-12">
    <motion.h2
      initial={{ opacity: 0, y: -20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8 }}
      className="text-4xl md:text-5xl font-extrabold text-center text-gray-800 mb-12"
    >
      Projects
    </motion.h2>

    <motion.div
      initial="hidden"
      animate="visible"
      className="max-w-6xl mx-auto grid gap-8 md:grid-cols-2 lg:grid-cols-3"
    >
      {projects.map((project, index) => (
        <motion.div
          key={project.name}
          custom={index}
          variants={cardVariants}
          className="bg-white rounded-2xl p-6 shadow-lg hover:shadow-2xl transform hover:-translate-y-2 transition-all duration-300 flex flex-col justify-between"
        >
          <div className="space-y-4">
            <h3 className="text-2xl font-semibold text-indigo-600">
              {project.name}
            </h3>
            <p className="text-gray-600">{project.description}</p>
          </div>
          <div className="mt-6 flex gap-4">
            <a
              href={project.github}
              target="_blank"
              rel="noopener noreferrer"
              className="flex items-center gap-2 text-indigo-600 hover:text-indigo-800 font-medium transition-colors"
            >
              <Github size={20} /> GitHub
            </a>
            {project.live && (
              <a
                href={project.live}
                target="_blank"
                rel="noopener noreferrer"
                className="flex items-center gap-2 text-gray-600 hover:text-gray-800 font-medium transition-colors"
              >
                <ExternalLink size={20} /> Live Demo
              </a>
            )}
          </div>
        </motion.div>
      ))}
    </motion.div>
  </section>
);

export default Projects;
