import React from "react";

const projects = [
  {
    name: "MOODIFY – AI Song Recommender",
    description: "Mood-based song suggestions using emojis & Spotify API.",
    github: "https://github.com/Atharv28ye/moodify-song-recommender",
  },
  {
    name: "AI Tokenizer Tool",
    description: "Estimate token usage & cost for GPT models in real-time.",
    github: "https://github.com/Atharv28ye/ai-tokenizer", // Replace with actual link
  },
  {
    name: "SmartStudy AI",
    description: "Full-stack GPT-powered study assistant for students.",
    github: "https://github.com/Atharv28ye/Smartstudyai", // Replace with actual link
  },
  {
    name: "Custom LLM:GPT Genius",
    description: "React + Tailwind portfolio to showcase my work.",
    github: "https://github.com/Atharv28ye/llm-core-full", // Replace with actual link
  },
  {
    name: "Portfolio Website",
    description: "React + Tailwind portfolio to showcase my work..",
    github: "https://github.com/Atharv28ye/blog-platform", // Replace with actual link
  },
];

const Projects = () => {
  return (
    <section id="projects" className="bg-gray-50 py-20 px-6 md:px-12">
      <div className="max-w-6xl mx-auto">
        <h2 className="text-3xl md:text-4xl font-bold text-center text-gray-800 mb-12">
          Projects
        </h2>

        <div className="grid gap-8 md:grid-cols-2 lg:grid-cols-3">
          {projects.map((project, index) => (
            <div
              key={index}
              className="bg-white shadow-md hover:shadow-xl rounded-xl p-6 transition duration-300 flex flex-col justify-between"
            >
              <div>
                <h3 className="text-xl font-semibold text-indigo-600 mb-2">
                  {project.name}
                </h3>
                <p className="text-gray-600 text-sm mb-4">{project.description}</p>
              </div>
              <a
                href={project.github}
                target="_blank"
                rel="noopener noreferrer"
                className="mt-auto inline-block bg-indigo-600 text-white text-sm font-medium px-4 py-2 rounded hover:bg-indigo-700 transition"
              >
                View on GitHub
              </a>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
