import React from "react";
import { motion } from "framer-motion";

const skills = [
  "React.js",
  "Tailwind CSS",
  "JavaScript",
  "TypeScript",
  "Python",
  "Streamlit",
  "Flask",
  "Node.js",
  "Express",
  "MongoDB",
  "HTML5",
  "CSS3",
  "Postman",
  "Git & GitHub",
  "Framer Motion",
  "Vite",
  "OpenAI API",
  "Deep Learning",
  "Transformers",
  "LLM Ops & Fine-Tuning",
  "AI & ML",
  "NLP",
  "Data Analytics",
  "AWS Cloud",
  "Problem Solving",
  "UI/UX Design",
  "REST APIs",
  "Collaborative Dev",
];

const container = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: { staggerChildren: 0.1, when: "beforeChildren" },
  },
};

const item = {
  hidden: { opacity: 0, scale: 0.8, y: 20 },
  visible: { opacity: 1, scale: 1, y: 0, transition: { type: "spring", stiffness: 120 } },
};

const Skills = () => (
  <section id="skills" className="relative bg-gradient-to-br from-purple-700 to-pink-600 py-24 px-6 md:px-12 overflow-hidden">
    {/* Subtle floating circles */}
    <motion.div
      className="absolute -left-10 -top-10 w-40 h-40 bg-pink-500 rounded-full opacity-20 animate-pulse"
      animate={{ scale: [1, 1.2, 1] }}
      transition={{ duration: 4, repeat: Infinity }}
    />
    <motion.div
      className="absolute right-0 bottom-0 w-60 h-60 bg-purple-500 rounded-full opacity-20 animate-pulse delay-200"
      animate={{ scale: [1, 1.2, 1] }}
      transition={{ duration: 5, repeat: Infinity }}
    />

    <motion.h2
      initial={{ opacity: 0, y: -20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8 }}
      className="text-4xl md:text-5xl text-white font-extrabold text-center mb-12"
    >
      Tech Stack & Tools
    </motion.h2>

    <motion.div
      variants={container}
      initial="hidden"
      animate="visible"
      className="max-w-5xl mx-auto grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-4 gap-6"
    >
      {skills.map((skill, i) => (
        <motion.div
          key={skill}
          variants={item}
          whileHover={{ scale: 1.1, y: -5 }}
          className="relative bg-white/20 backdrop-blur-md rounded-lg py-3 px-4 text-center text-white font-medium cursor-default shadow-lg hover:shadow-2xl transition"
        >
          <motion.span
            initial={{ color: "#FFFFFF" }}
            animate={{ color: ["#FFE4E1", "#FFD1DC", "#FFC0CB", "#FFE4E1"] }}
            transition={{ repeat: Infinity, duration: 4, delay: i * 0.1 }}
            className="text-sm"
          >
            {skill}
          </motion.span>
          <motion.div
            className="absolute -top-2 -right-2 w-3 h-3 bg-white rounded-full"
            animate={{ x: [0, 4, 0], y: [0, -4, 0] }}
            transition={{ repeat: Infinity, duration: 2, delay: i * 0.2 }}
          />
        </motion.div>
      ))}
    </motion.div>
  </section>
);

export default Skills;
