import React from "react";
import { motion } from "framer-motion";
import { Github } from "lucide-react";

const projects = [
  {
    name: "MOODIFY – AI Song Recommender",
    description: "Mood-based song suggestions using emojis & Spotify API.",
    github: "https://github.com/Atharv28ye/moodify-song-recommender",
  },
  {
    name: "AI Tokenizer Tool",
    description: "Estimate token usage & cost for GPT models in real-time.",
    github: "https://github.com/Atharv28ye/ai-tokenizer",
  },
  {
    name: "SmartStudy AI",
    description: "Full-stack GPT-powered study assistant for students.",
    github: "https://github.com/Atharv28ye/Smartstudyai",
  },
  {
    name: "Custom LLM: GPT Genius",
    description: "Built a custom LLM from scratch—tokenizer & math engine.",
    github: "https://github.com/Atharv28ye/llm-core-full",
  },
  {
    name: "Portfolio Website",
    description: "React + Tailwind portfolio showcasing my projects.",
    github: "https://github.com/Atharv28ye/blog-platform",
  },
];

const cardVariants = {
  hidden: { opacity: 0, scale: 0.9 },
  visible: (i) => ({
    opacity: 1,
    scale: 1,
    transition: { delay: i * 0.2, type: "spring", stiffness: 120 },
  }),
};

const Projects = () => (
  <section id="projects" className="bg-gray-50 py-24 px-6 md:px-12">
    <motion.h2
      initial={{ opacity: 0, y: -30 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8 }}
      className="text-4xl md:text-5xl font-extrabold text-center text-gray-800 mb-16"
    >
      Projects
    </motion.h2>

    <motion.div
      initial="hidden"
      animate="visible"
      className="max-w-6xl mx-auto grid gap-10 sm:grid-cols-2 lg:grid-cols-3"
    >
      {projects.map((project, index) => (
        <motion.div
          key={project.name}
          custom={index}
          variants={cardVariants}
          className="relative bg-white rounded-2xl p-6 shadow-lg hover:shadow-2xl transform hover:-translate-y-2 transition-all duration-300 flex flex-col"
        >
          {/* Animated Accent Bar */}
          <motion.div
            className="absolute top-0 left-0 h-1 w-16 bg-gradient-to-r from-pink-500 to-purple-600 rounded-br-lg"
            initial={{ width: 0 }}
            animate={{ width: "4rem" }}
            transition={{ delay: index * 0.2 + 0.3, duration: 0.6 }}
          />

          {/* Project Details */}
          <h3 className="mt-4 text-2xl font-semibold text-indigo-600">
            {project.name}
          </h3>
          <p className="mt-2 text-gray-600 flex-grow">{project.description}</p>

          {/* GitHub Link Button */}
          <motion.a
            href={project.github}
            target="_blank"
            rel="noopener noreferrer"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className="mt-6 inline-flex items-center justify-center gap-2 font-medium text-indigo-700 hover:text-indigo-900"
          >
            <Github size={20} />
            View on GitHub
          </motion.a>
        </motion.div>
      ))}
    </motion.div>
  </section>
);

export default Projects;
