import React from 'react';
import { motion } from 'framer-motion';
import { ArrowRight } from 'lucide-react';

const About = () => {
  return (
    <section id="about" className="bg-white py-20 px-6 md:px-12 overflow-hidden">
      {/* Section Title */}
      <motion.h2
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="text-3xl md:text-4xl font-bold text-gray-800 mb-6 text-center"
      >
        About Me
      </motion.h2>

      {/* Main Content */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1, delay: 0.3 }}
        className="max-w-4xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-8 items-center"
      >
        {/* Text Block */}
        <div className="space-y-4">
          <p className="text-lg text-gray-600 leading-relaxed">
            I’m Atharv Puranik—a computer engineering graduate and AI/ML enthusiast crafting intelligent systems end-to-end. From building a large
            language model from scratch (tokenizer & math engine) to deploying cloud-powered web apps, my work blends deep learning with modern
            full-stack development.
          </p>
          <p className="text-lg text-gray-600 leading-relaxed">
            My toolkit includes React.js, Tailwind CSS, JavaScript/TypeScript, Python (TensorFlow, PyTorch, Scikit-learn), Flask, Streamlit, Node.js,
            Express, MongoDB, and AWS cloud services. I’m AWS Academy certified in Cloud Foundations, Cloud Architecting, Generative AI, and
            Machine Learning Foundations.
          </p>
          <p className="text-lg text-gray-600 leading-relaxed">
            I thrive on solving complex problems, collaborating across teams, and delivering scalable, user-centric technology. Let’s build the next
            generation of AI-driven products together.
          </p>
        </div>

        {/* Skills & Certifications */}
        <motion.div
          initial={{ opacity: 0, x: 30 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1, delay: 0.6 }}
          className="flex flex-col space-y-6"
        >
          <div>
            <h3 className="text-xl font-semibold text-gray-800 mb-2">Core Skills</h3>
            <ul className="grid grid-cols-2 sm:grid-cols-3 gap-2 text-gray-600">
              {[
                'React.js',
                'Tailwind CSS',
                'JavaScript/TypeScript',
                'Python',
                'Flask & Streamlit',
                'Node.js & Express',
                'MongoDB',
                'AWS Cloud',
                'TensorFlow & PyTorch',
                'NLP & Generative AI',
                'Git & GitHub',
                'UI/UX Design'
              ].map(skill => (
                <li key={skill} className="flex items-center">
                  <ArrowRight size={16} className="mr-2 text-pink-500" />
                  {skill}
                </li>
              ))}
            </ul>
          </div>

          <div>
            <h3 className="text-xl font-semibold text-gray-800 mb-2">AWS Academy Certifications</h3>
            <ul className="list-disc list-inside text-gray-600 space-y-1">
              <li>Cloud Foundations</li>
              <li>Cloud Architecting</li>
              <li>Introduction to Generative AI</li>
              <li>Foundations in Machine Learning</li>
            </ul>
          </div>
        </motion.div>
      </motion.div>
    </section>
  );
};

export default About;
